import HeaderCatalog from "./HeaderCatalog/header_catalog";
import FilterCatalog from "./FilterCatalog/filter_catalog";
import BottomCatalog from "./BottomCatalog/bottom_catalog";
import Footer from "../Home/Footer/footer";
import React, { useState, useContext } from 'react';
import Trolleybuses from "./Trolleybuses/trolleybuses";
import { CatalogContext } from './CatalogContext'; // Приклад контексту (якщо ви вирішили використовувати контекст)

function Catalog() {
    const trolleybusesItemList = [
        { id: 1, img: <img src="/icons/big_trolleybus.png" alt="photo1" width="370" height="220"/>, title: 'TRHD-533', description:'hrrehre', price: 10.00, type: 'for_50people' },
        { id: 2, title: 'GYUJNE-222', description:'hetfd', price: 8.00, type: 'for_70people' },
        { id: 3, title: '76DJHG', description:'kinjte', price: 2.00, type: 'for_30people' },
        { id: 4, title: 'GYEBGEY', description:'rfvghuytrdhjytfd', price: 45.00, type: 'for_70people' },
        { id: 5, title: 'GVWUU DHU', description:'kikuytffgytrfdfghytrfrdrgtr', price: 55.00, type: 'for_50people' },
        { id: 6, title: '20-HGFff', description:'arggr', price: 67.00, type: 'for_30people' },
    ];


    const [filter, setFilter] = useState('all'); // Стан для фільтрації за властивостями (замість 'all' ви можете поставити дефолтне значення)
    const [searchText, setSearchText] = useState(''); // Стан для текстового пошуку

    // Отримання списку елементів із стану або контексту
    // const { trolleybusesItemList } = useContext(CatalogContext); // Приклад використання контексту

    // Функція для фільтрації елементів
    const filteredItems = trolleybusesItemList.filter((trolleybus) => {
        if (filter === 'all') return true;
        return trolleybus.type === filter; // Приклад фільтрації за типом
    });

    // Функція для текстового пошуку
    const searchedItems = filteredItems.filter((trolleybus) => {
        if (searchText === '') return true;
        return trolleybus.title.toLowerCase().includes(searchText.toLowerCase()); // Пошук за назвою (регістронезалежний)
    });

    return (
        <div>
            <div>
                <HeaderCatalog/>
                <FilterCatalog/>
                <div className="product-list">
                    {trolleybusesItemList.map((trolleybuses) => (
                        <Trolleybuses key={trolleybuses.id} product={trolleybuses} />
                    ))}
                </div>
                <BottomCatalog/>
                <Footer/>
            </div>
            <div className="filter-section">
                {/* Вибір фільтру за типом */}
                <select value={filter} onChange={(e) => setFilter(e.target.value)}>
                    <option value="all">All</option>
                    <option value="for_50people">For 50 People</option>
                    <option value="for_70people">For 70 People</option>
                    <option value="for_30people">For 30 People</option>
                    {/* Додайте інші опції за необхідності */}
                </select>
                {/* Текстовий пошук */}
                <input
                    type="text"
                    value={searchText}
                    onChange={(e) => setSearchText(e.target.value)}
                    placeholder="Search by title"
                />
            </div>
            {/* Відображення відфільтрованих і відфільтрованих елементів */}
            <div className="product-list">
                {searchedItems.map((trolleybus) => (
                    <Trolleybuses key={trolleybus.id} product={trolleybus} />
                ))}
            </div>
        </div>
    );
}

export default Catalog;
