import HeaderCatalog from "./HeaderCatalog/header_catalog";
import FilterCatalog from "./FilterCatalog/filter_catalog";
import BottomCatalog from "./BottomCatalog/bottom_catalog";
import Footer from "../Home/Footer/footer";
import React, {useState} from "react";
import Trolleybuses from "./Trolleybuses/trolleybuses";

function Catalog() {
    const [trolleybusesItemList] = useState([
        { id: 1, img: <img src="/icons/big_trolleybus.png" alt="photo1" width="370" height="220"/>, title: 'TRHD-533', description:'hrrehre', price: 10.00, type: 'for_50people' },
        { id: 2, title: 'GYUJNE-222', description:'hetfd', price: 8, type: 'for_70people' },
        { id: 3, title: '76DJHG', description:'kinjte', price: 2, type: 'for_30people' },
        { id: 4, title: 'GYEBGEY', description:'rfvghuytrdhjytfd', price: 45, type: 'for_70people' },
        { id: 5, title: 'GVWUU DHU', description:'kikuytffgytrfdfghytrfrdrgtr', price: 55, type: 'for_50people' },
        { id: 6, title: '20-HGFff', description:'arggr', price: 67, type: 'for_30people' },
    ]);

    const [selectedPriceOption, setSelectedPriceOption] = useState('all');
    const [selectedTitleOption, setSelectedTitleOption] = useState('all');
    const [selectedTypeOption, setSelectedTypeOption] = useState('all');
    const [searchText, setSearchText] = useState('');

    const [selectedFilters, setSelectedFilters] = useState({
        price: 'all',
        title: 'all',
        type: 'all',
    });

    const clearSearch = () => {
        setSearchText('');
    };

    const handleFilterChange = (filterType, value) => {
        setFilters((prevFilters) => ({
            ...prevFilters,
            [filterType]: value,
        }));
    };

    const clearFilters = () => {
        setFilters({
            price: 'all',
            title: 'all',
            type: 'all',
        });
        setSearchText('');
    };

    const applyFilters = () => {
        const filteredTrolleybuses = trolleybusesItemList.filter((trolleybus) => {
            const matchesPrice =
                selectedFilters.price === 'all' ||
                (selectedFilters.price === 'more_than_50' && trolleybus.price > 50) ||
                (selectedFilters.price === 'more_than_10' && trolleybus.price > 10) ||
                (selectedFilters.price === 'more_than_2' && trolleybus.price > 2);

            const matchesTitle =
                selectedFilters.title === 'all' ||
                (selectedFilters.title === 'with_number' && /\d/.test(trolleybus.title)) ||
                (selectedFilters.title === 'without_number' && !/\d/.test(trolleybus.title)) ||
                (selectedFilters.title === 'uppercase' && /^[^a-z]*$/.test(trolleybus.title));

            const matchesType = selectedFilters.type === 'all' || trolleybus.type.includes(selectedFilters.type);
            const matchesSearch = searchText === '' || trolleybus.title.toLowerCase().includes(searchText);

            return matchesPrice && matchesTitle && matchesType && matchesSearch;
        });

        return filteredTrolleybuses;
    };

    return (
        <div>
            <div>
                <HeaderCatalog onSearchChange={setSearchText} onClearSearch={clearSearch} />
                <FilterCatalog
                    onPriceChange={setSelectedPriceOption}
                    onTitleChange={setSelectedTitleOption}
                    onTypeChange={setSelectedTypeOption}
                    onSearchChange={setSearchText}
                />
                <div className="product-list">
                    {applyFilters().map((trolleybus) => (
                        <Trolleybuses key={trolleybus.id} product={trolleybus} />
                    ))}
                </div>
                <BottomCatalog />
                <Footer />
            </div>
        </div>
    );
}

export default Catalog;
