import React from 'react';
import Select from '../../Buttons/select';

function FilterCatalog({ onOptionChange, priceFilter, titleFilter, typeFilter, onCancelFilters, onClearFilters }) {
    const [selectedFilters, setSelectedFilters] = React.useState({
        price: priceFilter,
        title: titleFilter,
        type: typeFilter,
    });

    const handleApplyFilters = () => {
        onOptionChange('price', selectedFilters.price);
        onOptionChange('title', selectedFilters.title);
        onOptionChange('type', selectedFilters.type);
    };

    const handleCancelFilters = () => {
        setSelectedFilters({
            price: priceFilter,
            title: titleFilter,
            type: typeFilter,
        });
    };

    return (
        <div className="filter_catalog">
            <nav className="header_catalog__nav1">
                <Select
                    options={[
                        { label: 'Price', value: 'price' },
                        { label: 'More than 50', value: 'more_than_50' },
                        { label: 'More than 10', value: 'more_than_10' },
                        { label: 'More than 2', value: 'more_than_2' },
                    ]}
                    value={selectedFilters.price}
                    onChange={(event) => setSelectedFilters({ ...selectedFilters, price: event.target.value })}
                />
            </nav>
            <nav className="header_catalog__nav2">
                <Select
                    options={[
                        { label: 'Title', value: 'title' },
                        { label: 'With a number', value: 'with_number' },
                        { label: 'Without a number', value: 'without_number' },
                        { label: 'With big letters', value: 'uppercase' },
                    ]}
                    value={selectedFilters.title}
                    onChange={(event) => setSelectedFilters({ ...selectedFilters, title: event.target.value })}
                />
            </nav>
            <nav className="header_catalog__nav3">
                <Select
                    options={[
                        { label: 'Type', value: 'type' },
                        { label: 'For 70 people', value: 'for_70people' },
                        { label: 'For 30 people', value: 'for_30people' },
                        { label: 'For 50 people', value: 'for_50people' },
                    ]}
                    value={selectedFilters.type}
                    onChange={(event) => setSelectedFilters({ ...selectedFilters, type: event.target.value })}
                />
            </nav>
            <button className="button_apply" onClick={handleApplyFilters}>Apply</button>
            <button className="button_cancel" onClick={handleCancelFilters}>Cancel</button>
        </div>
    );
}

export default FilterCatalog;
