import React, { useState } from "react";
import Trolleybuses from "./Trolleybuses/trolleybuses";
import HeaderCatalog from "./HeaderCatalog/header_catalog";
import FilterCatalog from "./FilterCatalog/filter_catalog";
import BottomCatalog from "./BottomCatalog/bottom_catalog";
import Footer from "../Home/Footer/footer";
import {Link, Outlet} from "react-router-dom";

function Catalog() {

    const [trolleybusesItemList] = useState([
        { id: 1, img: <img src="/icons/big_trolleybus.png" alt="photo1" width="370" height="220" />, title: 'TRHD-533', description: 'hrrehre', price: 10.00, type: 'for_50people' },
        { id: 2, title: 'GYUJNE-222', description: 'hetfd', price: 8, type: 'for_70people' },
        { id: 3, title: '76DJHG', description: 'kinjte', price: 2, type: 'for_30people' },
        { id: 4, title: 'GYEBGEY', description: 'rfvghuytrdhjytfd', price: 45, type: 'for_70people' },
        { id: 5, title: 'GVWUU DHU', description: 'kikuytffgytrfdfghytrfrdrgtr', price: 55, type: 'for_50people' },
        { id: 6, title: '20-HGFff', description: 'arggr', price: 67, type: 'for_30people' },
    ]);

    const [filters, setFilters] = useState({
        price: 'all',
        title: 'all',
        type: 'all',
    });

    const [searchText, setSearchText] = useState('');

    const [filtersApplied, setFiltersApplied] = useState(false); // Новое состояние

    const applyFilters = () => {
        if (filtersApplied) { // Применять фильтры только если filtersApplied === true
            return trolleybusesItemList.filter((trolleybus) => {
                const priceFilterCondition =
                    filters.price === 'all' ||
                    (filters.price === 'more_than_50' && trolleybus.price > 50) ||
                    (filters.price === 'more_than_10' && trolleybus.price > 10) ||
                    (filters.price === 'more_than_2' && trolleybus.price > 2);

                const titleFilterCondition =
                    filters.title === 'all' ||
                    (filters.title === 'with_number' && /\d/.test(trolleybus.title)) ||
                    (filters.title === 'without_number' && !/\d/.test(trolleybus.title)) ||
                    (filters.title === 'uppercase' && /^[^a-z]*$/.test(trolleybus.title));

                const typeFilterCondition =
                    filters.type === 'all' || filters.type === trolleybus.type;

                const searchFilterCondition =
                    searchText === '' || trolleybus.title.toLowerCase().includes(searchText);

                return priceFilterCondition && titleFilterCondition && typeFilterCondition && searchFilterCondition;
            });
        } else {
            return trolleybusesItemList; // Если фильтры не применены, возвращаем исходный список
        }
    };

    const filteredTrolleybuses = applyFilters();

    const handleApplyFilters = () => {
        setFiltersApplied(true); // Устанавливаем filtersApplied в true
    };

    const handleCancelFilters = () => {
        setFiltersApplied(false); // Сбрасываем filtersApplied в false
    };

    return (
        <div>
            <HeaderCatalog
                onSearchChange={(value) => setSearchText(value)}
                onClearSearch={() => setSearchText('')}
            />
            <FilterCatalog
                onOptionChange={(filterType, event) =>
                    setFilters((prevFilters) => ({ ...prevFilters, [filterType]: event.target.value }))
                }
                priceFilter={filters.price}
                titleFilter={filters.title}
                typeFilter={filters.type}
                onCancelFilters={handleCancelFilters}
            />
            <div className="product-list">
                {filteredTrolleybuses.map((trolleybus) => (
                    <Link to={`/items/${trolleybus.id}`} key={trolleybus.id}>
                        <Trolleybuses product={trolleybus} />
                    </Link>
                ))}
            </div>
            <button className="button_apply" onClick={handleApplyFilters}>
                Apply
            </button>
            <BottomCatalog />
            <Footer />
            <Outlet />
        </div>
    );
}

export default Catalog;
