import React, { useState } from "react";
import Trolleybuses from "./Trolleybuses/trolleybuses";
import HeaderCatalog from "./HeaderCatalog/header_catalog";
import FilterCatalog from "./FilterCatalog/filter_catalog";
import BottomCatalog from "./BottomCatalog/bottom_catalog";
import Footer from "../Home/Footer/footer";

function Catalog() {
    const [trolleybusesItemList] = useState([
        { id: 1, img: <img src="/icons/big_trolleybus.png" alt="photo1" width="370" height="220" />, title: 'TRHD-533', description: 'hrrehre', price: 10.00, type: 'for_50people' },
        { id: 2, title: 'GYUJNE-222', description: 'hetfd', price: 8, type: 'for_70people' },
        { id: 3, title: '76DJHG', description: 'kinjte', price: 2, type: 'for_30people' },
        { id: 4, title: 'GYEBGEY', description: 'rfvghuytrdhjytfd', price: 45, type: 'for_70people' },
        { id: 5, title: 'GVWUU DHU', description: 'kikuytffgytrfdfghytrfrdrgtr', price: 55, type: 'for_50people' },
        { id: 6, title: '20-HGFff', description: 'arggr', price: 67, type: 'for_30people' },
    ]);

    const [filters, setFilters] = useState({
        price: 'all',
        title: 'all',
        type: 'all',
    });

    const [priceFilter, setPriceFilter] = useState('all');
    const [titleFilter, setTitleFilter] = useState('all');
    const [typeFilter, setTypeFilter] = useState('all');

    const handleOptionChange = (event) => {
        setPriceFilter(event.target.value);
    };

    const handleApplyFilters = () => {
        // Ваша функція, яка використовує фільтри для відображення відфільтрованих об'єктів
    };

    const clearFilters = () => {
        setFilters({
            price: 'all',
            title: 'all',
            type: 'all',
        });
        setSearchText('');
    };


    const applyFilters = () => {
        return trolleybusesItemList.filter((trolleybus) => {
            const priceFilterCondition = // Умови фільтрації по ціні
                priceFilter === 'all' ||
                (priceFilter === 'more_than_50' && trolleybus.price > 50) ||
                (priceFilter === 'more_than_10' && trolleybus.price > 10) ||
                (priceFilter === 'more_than_2' && trolleybus.price > 2);

            const titleFilterCondition = // Умови фільтрації по назві
                titleFilter === 'all' ||
                (titleFilter === 'with_number' && /\d/.test(trolleybus.title)) ||
                (titleFilter === 'without_number' && !/\d/.test(trolleybus.title)) ||
                (titleFilter === 'uppercase' && /^[^a-z]*$/.test(trolleybus.title));

            const typeFilterCondition = // Умови фільтрації по типу
                typeFilter === 'all' ||
                (typeFilter === 'for_70people' && trolleybus.type === 'for_70people') ||
                (typeFilter === 'for_30people' && trolleybus.type === 'for_30people') ||
                (typeFilter === 'for_50people' && trolleybus.type === 'for_50people');

            const searchFilterCondition = // Умови фільтрації по тексту
                searchText === '' || trolleybus.title.toLowerCase().includes(searchText);

            return priceFilterCondition && titleFilterCondition && typeFilterCondition && searchFilterCondition;
        });
    };

    const filteredTrolleybuses = applyFilters();


    return (
        <div>
            <HeaderCatalog
                onSearchChange={(value) => setSearchText(value)}
                onClearSearch={() => setSearchText('')}
            />
            <FilterCatalog
                onOptionChange={handleOptionChange}
                onApplyFilters={handleApplyFilters}
                priceFilter={priceFilter}
                titleFilter={titleFilter}
                typeFilter={typeFilter}
            />
            <div className="product-list">
                {trolleybusesItemList.map((trolleybus) => (
                    <Trolleybuses key={trolleybus.id} product={trolleybus} />
                ))}
            </div>
            <BottomCatalog />
            <Footer />
        </div>
    );
}

export default Catalog;
