import HeaderCatalog from "./HeaderCatalog/header_catalog";
import FilterCatalog from "./FilterCatalog/filter_catalog";
import BottomCatalog from "./BottomCatalog/bottom_catalog";
import Footer from "../Home/Footer/footer";
import React, {useState} from "react";
import Trolleybuses from "./Trolleybuses/trolleybuses";

function Catalog() {
    const [items, setItems] = useState( [
        { id: 1, img: <img src="/icons/big_trolleybus.png" alt="photo1" width="370" height="220"/>, title: 'TRHD-533', description:'hrrehre', price: 10.00, type: 'for_50people' },
        { id: 2, title: 'GYUJNE-222', description:'hetfd', price: 8.00, type: 'for_70people' },
        { id: 3, title: '76DJHG', description:'kinjte', price: 2.00, type: 'for_30people' },
        { id: 4, title: 'GYEBGEY', description:'rfvghuytrdhjytfd', price: 45.00, type: 'for_70people' },
        { id: 5, title: 'GVWUU DHU', description:'kikuytffgytrfdfghytrfrdrgtr', price: 55.00, type: 'for_50people' },
        { id: 6, title: '20-HGFff', description:'arggr', price: 67.00, type: 'for_30people' },
    ]);

    const [selectedPriceOption, setSelectedPriceOption] = useState('all');
    const [selectedTitleOption, setSelectedTitleOption] = useState('all');
    const [selectedTypeOption, setSelectedTypeOption] = useState('all');
    const [searchText, setSearchText] = useState('');


    const filteredItems = items.filter((item) => {
        const matchesPrice = selectedPriceOption === 'all' || item.price > parseFloat(selectedPriceOption);
        const matchesTitle = selectedTitleOption === 'all' || item.title.includes(selectedTitleOption);
        const matchesType = selectedTypeOption === 'all' || item.type.includes(selectedTypeOption);
        const matchesSearch = searchText === '' || item.title.includes(searchText);

        return matchesPrice && matchesTitle && matchesType && matchesSearch;
    });


    return (
        <div>
            <div>
                <HeaderCatalog/>
                <FilterCatalog
                    onPriceChange={setSelectedPriceOption}
                    onTitleChange={setSelectedTitleOption}
                    onTypeChange={setSelectedTypeOption}
                    onSearchChange={setSearchText}
                />
                <div className="product-list">
                    {filteredItems.map((trolleybus) => (
                        <Trolleybuses key={trolleybus.id} product={trolleybus} />
                    ))}
                </div>
                <BottomCatalog/>
                <Footer/>
            </div>
        </div>
    );
}

export default Catalog;
