import React, { useState } from 'react';

function Header() {
    const [isHomeButtonWhite, setHomeButtonWhite] = useState(true);
    const [isCatalogButtonWhite, setCatalogButtonWhite] = useState(true);
    const [isCartButtonWhite, setCartButtonWhite] = useState(true);

    const handleHomeButtonClick = () => {
        setHomeButtonWhite(true);
        setCatalogButtonWhite(false);
        setCartButtonWhite(false);
    };

    const handleCatalogButtonClick = () => {
        setHomeButtonWhite(false);
        setCatalogButtonWhite(true);
        setCartButtonWhite(false);
    };

    const handleCartButtonClick = () => {
        setHomeButtonWhite(false);
        setCatalogButtonWhite(false);
        setCartButtonWhite(true);
    };

    return (
        <header>
            <div className="header">
                <nav className="header__nav">
                    <div className="header__nav-logo">
                        <img src="/icons/trolleybus-icon.png" alt="header_logo" width="80" height="80" />
                    </div>
                    <div className="header__nav-buttons">
                        <h2>
                            <button
                                type="button"
                                className={`header__category1 ${isHomeButtonWhite ? '' : 'white'}`}
                                onClick={handleHomeButtonClick}
                            >
                                <a href="http://localhost:3000/Home">Home</a>
                            </button>
                        </h2>
                        <h2>
                            <button
                                type="button"
                                className={`header__category ${isCatalogButtonWhite ? '' : 'white'}`}
                                onClick={handleCatalogButtonClick}
                            >
                                <a href="http://localhost:3000/Catalog">Catalog</a>
                            </button>
                        </h2>
                        <h2>
                            <button
                                type="button"
                                className={`header__category ${isCartButtonWhite ? '' : 'white'}`}
                                onClick={handleCartButtonClick}
                            >
                                <a href="http://localhost:3000/Cart">Cart</a>
                            </button>
                        </h2>
                    </div>
                </nav>
            </div>
        </header>
    );
}

export default Header;
