import Select from "../Buttons/select";
import PrimaryButton from "../Buttons/primary_button";
import React, {useState} from "react";

function FilterCatalog() {
    const products = [
        { id: 1, name: 'Product 1', price: 10.00 },
        { id: 2, name: 'Product 2', price: 20.00 },
        { id: 3, name: 'Product 3', price: 15.00 },
    ];

    const [selectedOption, setSelectedOption] = useState('all');

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const filteredProducts = selectedOption === 'all' ? products : products.filter(product => product.name.includes(selectedOption));

    return (
        <div className="filter_catalog">
            <nav className="header_catalog__nav1">
                <Select
                    options={[
                        { label: 'All', value: 'all' },
                        { label: 'Product 1', value: 'Product 1' },
                        { label: 'Product 2', value: 'Product 2' },
                        { label: 'Product 3', value: 'Product 3' },
                    ]}
                    value={selectedOption}
                    onChange={handleOptionChange}
                />
                <ul>
                    {filteredProducts.map((product) => (
                        <li key={product.id}>
                            {product.name} - ${product.price}
                        </li>
                    ))}
                </ul>
            </nav>
            <nav className="header_catalog__nav2">
                <Select
                    options={[
                        { label: 'All', value: 'all' },
                        { label: 'Product 1', value: 'Product 1' },
                        { label: 'Product 2', value: 'Product 2' },
                        { label: 'Product 3', value: 'Product 3' },
                    ]}
                    value={selectedOption}
                    onChange={handleOptionChange}
                />
                <ul>
                    {filteredProducts.map((product) => (
                        <li key={product.id}>
                            {product.name} - ${product.price}
                        </li>
                    ))}
                </ul>
            </nav>
            <nav className="header_catalog__nav3">
                <Select
                    options={[
                        { label: 'All', value: 'all' },
                        { label: 'Product 1', value: 'Product 1' },
                        { label: 'Product 2', value: 'Product 2' },
                        { label: 'Product 3', value: 'Product 3' },
                    ]}
                    value={selectedOption}
                    onChange={handleOptionChange}
                />
                <ul>
                    {filteredProducts.map((product) => (
                        <li key={product.id}>
                            {product.name} - ${product.price}
                        </li>
                    ))}
                </ul>
            </nav>
        </div>
    );
}

export default FilterCatalog;
