import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import HeaderCatalog from "../HeaderCatalog/header_catalog";
import Select from "../../Buttons/select";

function ItemPage({ trolleybusesItemList }) {
    const { id } = useParams();
    const itemId = parseInt(id);
    const selectedTrolleybus = trolleybusesItemList.find(trolleybus => trolleybus.id === itemId);

    if (!selectedTrolleybus) {
        return <div>Error</div>;
    }

    const [selectedColor, setSelectedColor] = useState(''); // Стан для збереження обраного кольору

    const colorOptions = [ // Опції для вибору кольору
        { label: 'Colour', value: 'colour' },
        { label: 'Black', value: 'colour_black' },
        { label: 'Red', value: 'colour_red' },
        { label: 'Green', value: 'colour_green' },
        { label: 'Blue', value: 'colour_blue' },
        { label: 'White', value: 'colour_white' },
    ];

    const handleColorChange = (event) => {
        setSelectedColor(event.target.value); // Зберігаємо обраний кольор в стані
    };

    return (
        <div>
            <HeaderCatalog/>
            <div className="img_desc_title">
                <img src="/icons/trolleybus_photo.png" alt="Trolleybus" width="370" height="220" />
                <div className="buttons_title_desc">
                    <div className="buttons_charac_1_2">
                        <button type="button" className="charact_button1">1 characteristic</button>
                        <button type="button" className="charact_button2">2 characteristic</button>
                    </div>
                    <div className="title_desc">
                        <h2>{selectedTrolleybus.title}</h2>
                        <p>Description: {selectedTrolleybus.description}</p>
                    </div>
                    <p>Trolleybuses was sold:</p>
                    <output className="output_num_sold_trolleybuses">2343</output>
                    <p>Color:</p>
                    <Select
                        options={colorOptions}
                        value={selectedColor}
                        onChange={handleColorChange}
                    />
                </div>
            </div>
            <p>Price: ${selectedTrolleybus.price}</p>
            <p>Type: {selectedTrolleybus.type}</p>
            <Link to="/Catalog">Back to Catalog</Link>
        </div>
    );
}

export default ItemPage;
