import React, { useState } from 'react';
import PrimaryButton from '../Buttons/primary_button';
import Select from '../Buttons/select';

function PhotosPart() {
    const options = [
        { value: 'big_trolleybuses', label: 'Big trolleybuses' },
        { value: 'ordinary_trolleybuses', label: 'Ordinary trolleybuses' },
        { value: 'new_trolleybuses', label: 'New trolleybuses' },
    ];

    const [selectedOption, setSelectedOption] = useState(options[0].value);

    const handleOptionChange = (optionValue) => {
        setSelectedOption(optionValue);
    };

    return (
        <div className="photos_part">
            <nav className="photos_part__nav">
                {options.map((option) => (
                    <div key={option.value} className={`photos_part__nav_i_h_p ${option.value === selectedOption ? 'active' : ''}`}>
                        <img src={`/icons/${option.value}.png`} alt={option.label} width="370" height="220" />
                        <div className={`photos_part_h_p ${option.value}`}>
                            <h1 className={`photos_part_header ${option.value}`}>
                                {option.label}
                            </h1>
                            <p className={`photos_part_paragraph ${option.value}`}>
                                {/* Опис для кожного типу тролейбуса */}
                            </p>
                        </div>
                        <PrimaryButton onClick={() => handleOptionChange(option.value)}>Select</PrimaryButton>
                    </div>
                ))}
            </nav>
            <Select options={options} value={selectedOption} onChange={handleOptionChange} />
            <div className="view_more_button"><a href="#View more">View more</a></div>
        </div>
    );
}

export default PhotosPart;
