import React, { useState } from "react";
import Trolleybuses from "./Trolleybuses/trolleybuses";
import HeaderCatalog from "./HeaderCatalog/header_catalog";
import FilterCatalog from "./FilterCatalog/filter_catalog";
import BottomCatalog from "./BottomCatalog/bottom_catalog";
import Footer from "../Home/Footer/footer";

function Catalog() {
    const [trolleybusesItemList] = useState([
        { id: 1, img: <img src="/icons/big_trolleybus.png" alt="photo1" width="370" height="220" />, title: 'TRHD-533', description: 'hrrehre', price: 10.00, type: 'for_50people' },
        { id: 2, title: 'GYUJNE-222', description: 'hetfd', price: 8, type: 'for_70people' },
        { id: 3, title: '76DJHG', description: 'kinjte', price: 2, type: 'for_30people' },
        { id: 4, title: 'GYEBGEY', description: 'rfvghuytrdhjytfd', price: 45, type: 'for_70people' },
        { id: 5, title: 'GVWUU DHU', description: 'kikuytffgytrfdfghytrfrdrgtr', price: 55, type: 'for_50people' },
        { id: 6, title: '20-HGFff', description: 'arggr', price: 67, type: 'for_30people' },
    ]);

    const [filteredTrolleybuses, setFilteredTrolleybuses] = useState([...trolleybusesItemList]);
    const [filters, setFilters] = useState({
        price: 'all',
        title: 'all',
        type: 'all',
    });

    const [searchText, setSearchText] = useState('');

    const handleFilterChange = (filterType, value) => {
        setFilters((prevFilters) => ({
            ...prevFilters,
            [filterType]: value,
        }));
    };

    const handleSearchChange = (searchText) => {
        setSearchText(searchText);
        updateFilteredTrolleybuses();
    };

    const updateFilteredTrolleybuses = () => {
        const filteredItems = applyFilters().filter((trolleybus) =>
            trolleybus.title.toLowerCase().includes(searchText.toLowerCase())
        );
        setFilteredTrolleybuses(filteredItems);
    };

    const clearFilters = () => {
        setFilters({
            price: 'all',
            title: 'all',
            type: 'all',
        });
        setSearchText('');
    };

    const applyFilters = () => {
        return trolleybusesItemList.filter((trolleybus) => {
            const priceFilter =
                filters.price === 'all' ||
                (filters.price === 'more_than_50' && trolleybus.price > 50) ||
                (filters.price === 'more_than_10' && trolleybus.price > 10) ||
                (filters.price === 'more_than_2' && trolleybus.price > 2);

            const titleFilter =
                filters.title === 'all' ||
                (filters.title === 'with_number' && /\d/.test(trolleybus.title)) ||
                (filters.title === 'without_number' && !/\d/.test(trolleybus.title)) ||
                (filters.title === 'uppercase' && /^[^a-z]*$/.test(trolleybus.title));

            const typeFilter = filters.type === 'all' || trolleybus.type === filters.type;

            return priceFilter && titleFilter && typeFilter;
        });
    };

    return (
        <div>
            <HeaderCatalog
                onSearchChange={(value) => setSearchText(value)}
                onClearSearch={() => setSearchText('')}
                // onSearchChange={handleSearchChange}
                // onClearSearch={() => handleSearchChange('')}
            />
            <FilterCatalog onFilterChange={handleFilterChange} />
            <div className="product-list">
                {filteredTrolleybuses.map((trolleybus) => (
                    <Trolleybuses key={trolleybus.id} product={trolleybus} />
                ))}
            </div>
            <BottomCatalog />
            <Footer />
        </div>
    );
}

export default Catalog;
