// Оновлений FilterCatalog
import React, { useState } from 'react';
import Select from '../../Buttons/select';

function FilterCatalog({ onFilterChange, onClearFilters, onApplyFilters, filters }) {
    const [selectedPriceOption, setSelectedPriceOption] = useState(filters.price);
    const [selectedTitleOption, setSelectedTitleOption] = useState(filters.title);
    const [selectedTypeOption, setSelectedTypeOption] = useState(filters.type);

    const handleOptionChange = (event) => {
        setSelectedPriceOption(event.target.value);
    };

    const handleOptionChange2 = (event) => {
        setSelectedTitleOption(event.target.value);
    };

    const handleOptionChange3 = (event) => {
        setSelectedTypeOption(event.target.value);
    };

    return (
        <div className="filter_catalog">
            <nav className="header_catalog__nav1">
                <Select
                    options={[
                        { label: 'Price', value: 'all' },
                        { label: 'More than 50', value: 'more_than_50' },
                        { label: 'More than 10', value: 'more_than_10' },
                        { label: 'More than 2', value: 'more_than_2' },
                    ]}
                    value={selectedPriceOption}
                    onChange={handleOptionChange}
                />
            </nav>
            <nav className="header_catalog__nav2">
                <Select
                    options={[
                        { label: 'Title', value: 'all' },
                        { label: 'With a number', value: 'with_number' },
                        { label: 'Without a number', value: 'without_number' },
                        { label: 'With big letters', value: 'uppercase' },
                    ]}
                    value={selectedTitleOption}
                    onChange={handleOptionChange2}
                />
            </nav>
            <nav className="header_catalog__nav3">
                <Select
                    options={[
                        { label: 'Type', value: 'all' },
                        { label: 'For 70 people', value: 'for_70people' },
                        { label: 'For 30 people', value: 'for_30people' },
                        { label: 'For 50 people', value: 'for_50people' },
                    ]}
                    value={selectedTypeOption}
                    onChange={handleOptionChange3}
                />
            </nav>
            <button className="button_apply" onClick={onApplyFilters}>Apply</button>
        </div>
    );
}

export default FilterCatalog;
