import Select from "../Buttons/select";
import PrimaryButton from "../Buttons/primary_button";
import React, {useState} from "react";

function FilterCatalog() {
    const products = [
        { id: 1, name: 'Product 1', price: 10.0 },
        { id: 2, name: 'Product 2', price: 20.0 },
        { id: 3, name: 'Product 3', price: 15.0 },
    ];

    const [selectedOption, setSelectedOption] = useState('all');

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const filteredProducts = selectedOption === 'all' ? products : products.filter(product => product.name.includes(selectedOption));

    return (
        <div className="filter_catalog">
            <nav className="header_catalog__nav">
                 <div className="filter_catalog__nav-logo">
                     <img src="/icons/trolleybus-icon.png" alt="filter_catalog_logo" width="80" height="80" />
                 </div>
                 <div className="filter_catalog__nav-buttons">
                  </div>
                  <div className="button_apply">
                      <input className="apply_btn" type="search" placeholder="  Search..."></input>
                  </div>
            </nav>
            <Select
                options={[
                    { label: 'All', value: 'all' },
                    { label: 'Product 1', value: 'Product 1' },
                    { label: 'Product 2', value: 'Product 2' },
                    { label: 'Product 3', value: 'Product 3' },
                ]}
                value={selectedOption}
                onChange={handleOptionChange}
            />
            <ul>
                {filteredProducts.map((product) => (
                    <li key={product.id}>
                        {product.name} - ${product.price}
                    </li>
                ))}
            </ul>
            <PrimaryButton>View More</PrimaryButton>
        </div>
    );
}

export default FilterCatalog;
